import React, { useReducer } from "react";
import TaskContext from "../task-context";
import TaskReducer from '../task-reducer';
import {
  ADD_TASK,
  PROJECT_TASKS,
  VALIDATES_TASK,
  DELETE_TASK,
} from "../../../types";

const TaskState = props => {
  const initialState = {
    tasks: [
      {taskTitle: 'Elegir plataforma', status: true, projectId: 1 },
      {taskTitle: 'Elegir colores', status: false, projectId: 2 },
      {taskTitle: 'Elegir pago', status: false, projectId: 3 },
      {taskTitle: 'Elegir Hosting', status: true, projectId: 4 },
      {taskTitle: 'Elegir plataforma', status: true, projectId: 4 },
      {taskTitle: 'Elegir colores', status: false, projectId: 3 },
      {taskTitle: 'Elegir pago', status: false, projectId: 3 },
      {taskTitle: 'Elegir Hosting', status: true, projectId: 2 },
    ],
    projectTask: null,
    errorTask: false
  }
  // Creates dispatch
  const [state,dispatch] = useReducer(TaskReducer, initialState)

  //Crear Funciones
  //Obtener las tareas de un proyecto
  const getTasks = projectId => {
    dispatch({
      type: PROJECT_TASKS,
      payload: projectId
    })
  }

  //Adds task
  const addTask = task => {
    dispatch({
      type: ADD_TASK,
      payload: task
    })
  }

  //VALIDA Y MUESTRA ERROR
  const validatesTask = () => {
    dispatch({
      type: VALIDATES_TASK
    })
  }

  //DELETE TASK BY ID
  const deleteTask = id => {
    dispatch({
      type: DELETE_TASK,
      payload: id
    })
  }

  return(
    <TaskContext.Provider
      value={{
        tasks: state.tasks,
        projectTask : state.projectTask,
        errorTask: state.errorTask,
        getTasks,
        addTask,
        validatesTask,
        deleteTask
    }}>
      {props.children}
    </TaskContext.Provider>
  )
}

export default TaskState
