import React, {useReducer} from "react";

import { v4 as uuidv4 } from 'uuid';

import projectContext from "../project-context";
import projectReducer from "../project-reducer/index";
import {
  PROJECT_FORM,
  GET_PROJECTS,
  ADD_PROJECT,
  VALIDATES_FORM
} from '../../../types'


const ProjectState = props => {
  const projects = [
    {id: 1, title: 'Tienda Virtual'},
    {id: 2, title: 'Intranet'},
    {id: 3, title: 'Diseño de sitio Web'},
    {id: 4, title: 'MERN'}
  ]

  const initialState = {
    projects: [],
    form: false,
    errorform:false
  }

  //Dispatch para ejecutar las acciones
  const [state, dispatch] = useReducer( projectReducer, initialState )

  //Serie de funciones para el CRUD
  const showForm = () => {
    dispatch({
      type: PROJECT_FORM
    })
  }

  //Get projects
  const getProjects = () => {
    dispatch({
      type: GET_PROJECTS,
      payload: projects
    })
  }

  //ADD project
  const addProject = project => {
    project.id = uuidv4();

    //Add project in state
    dispatch({
      type: ADD_PROJECT,
      payload: project
    })
  }

  //Validates form
  const showErr = () => {
    dispatch({
      type: VALIDATES_FORM
    })
  }

  return(
    <projectContext.Provider
      value={{
        projects: state.projects,
        form: state.form,
        errorform: state.errorform,
        showForm,
        getProjects,
        addProject,
        showErr
      }}
    >
      {props.children}
    </projectContext.Provider>
  )
}

export default ProjectState;
