import React, { Fragment, useContext } from 'react';
import {
  Task,
} from "../single-task";

import projectContext from "../../../context/projects/project-context";

export const TaskList = () => {

  //Extract projects from initial state
  const projectsContext = useContext(projectContext);
  const { project, deleteProject } = projectsContext;

  //No selected project
  if(!project) return <h2>Selecciona un proyecto</h2>

  //Array destructuring to extract current project
  const [currentProject] = project;

  const projectTasks = [
    {taskTitle: 'Elegir plataforma', status: true },
    {taskTitle: 'Elegir colores', status: false },
    {taskTitle: 'Elegir pago', status: false },
    {taskTitle: 'Elegir Hosting', status: true },
  ]
//Deletes project
  const onClickDelete = () => {
    deleteProject(currentProject.id)
  }

  return (
    <Fragment>
      <h2>Proyecto: {currentProject.title}</h2>
      <ul className="listado-tareas">
        {projectTasks.length === 0
          ? <li className="tarea"><p>No hay tareas</p></li>
          : projectTasks.map(task => (
          <Task
            task={task}
          />
      ))}
      </ul>
      <button
        type="button"
        className="btn btn-eliminar"
        onClick={onClickDelete}
      >Eliminar proyecto &times;</button>
    </Fragment>
  );
};
