import React, { Fragment } from 'react';
import {
  Task,
} from "../single-task";

export const TaskList = () => {

  const projectTasks = [
    {taskTitle: 'Elegir plataforma', status: true },
    {taskTitle: 'Elegir colores', status: false },
    {taskTitle: 'Elegir pago', status: false },
    {taskTitle: 'Elegir Hosting', status: true },
  ]

  return (
    <Fragment>
      <h2>Proyecto: Tienda Virtual</h2>
      <ul className="listado-tareas">
        {projectTasks.length === 0} ? (<li className="tarea"><p>No hay tareas</p></li>)
        : projectTasks.map(task => (
          <Task
            task={task}
          />
      ))
      </ul>
    </Fragment>
  );
};
