import {
  PROJECT_TASKS
} from "../../../types";

export default (state, action) => {
  switch (action.type) {
    case PROJECT_TASKS:
      return {
        ...state,
        projectTask: state.tasks.filter()
      }
    default:
      return state;
  }
}
